mapper.insert:
INSERT INTO p_day
SET itodo = #{itodo},
repeat_day = #{repeatDay}

mapper.select:
SELECT itodo, iplant, deadline_time deadlineTime, deadline_date deadlineDate, ctnt, repeat_yn repeatYn
FROM p_todo
WHERE itodo = #{itodo}

SELECT repeat_day repeatDay
FROM p_day
WHERE itodo = #{itodo}

mapper.update:
UPDATE p_day
SET repeat_day = #{repeatDay}
WHERE itodo = #{itodo}

UPDATE p_todo
SET del_yn = 1
WHERE itodo = #{itodo}
